-- Teracgosas Rest: QuestReset etc.
-- Delete Quest Items and Staffs from inventory
DELETE w,e FROM `item_instance` AS w INNER JOIN `character_inventory` AS e ON w.`guid` = e.`item` WHERE `itemEntry` IN (71084, 71085, 71086, 69646, 69815, 71141, 69848);
-- Delete Queststatus to reset progress
DELETE FROM `character_queststatus` WHERE `quest` IN (29452, 29129, 29453, 29132, 29134, 29135, 29193, 29194, 29225, 29234, 29239, 29240, 29269, 29270, 29285, 29308, 29309, 29307, 29312);
